import React from "react";
import classes from './Cart.module.css'
import Modal from "../UI/Modal";
import { useContext, useState } from "react";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";
import Checkout from "./Checkout";

const Cart = props =>{

    const [isCheckingOut, setIsCheckingOut] = useState(false);
    const cartCtx = useContext(CartContext)

    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`
    const hasItems = cartCtx.items.length>0

    const cartItemRemoveHandler = id =>{
        cartCtx.removeItem(id);
    };
    const cartItemAddHandler = item =>{
        cartCtx.addItem({...item, amount: 1})
    };

    const orderHandler = () =>{
        setIsCheckingOut(true)

    }
    const modalActions = 
        <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
            {hasItems &&<button className={classes.button} onClick={orderHandler}>Order</button>}
        </div>

    const submitOrderHandler = (userData) =>{
        fetch('https://react-custom-hooks-1c7ed-default-rtdb.firebaseio.com/orders.json',{
            method: "POST",
            body: JSON.stringify({
                user: userData,
                orderedItems: cartCtx.items
            })
        });
    }

    const cartItems = <ul className={classes['cart-items']}>{cartCtx.items.map(item=>(<CartItem key={item.id} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler.bind(null, item.id)} onAdd={cartItemAddHandler.bind(null, item)} />))}</ul> 
    return (
        <Modal onClose={props.onClose}>
            {cartItems}
            <div>
                <span>Total Amount</span>
                <span>{totalAmount}</span>
            </div>
            {isCheckingOut  && <Checkout onConfirm = {submitOrderHandler} onCancel = {props.onClose} />}
            {!isCheckingOut && modalActions}    
        </Modal>
    )

}


export default Cart;
